mapper.update:
update ord
set ordstts = #{ordstts}
where ordno = #{ordno}

mapper.select:

select rnum, ordno, memno, ordstts, orddate, ordprice, discprice, finprice
from (
select rownum as rnum, ordno, memno, ordstts, orddate, ordprice, discprice, finprice
from (
select ordno, memno, ordstts, orddate, ordprice, discprice, finprice
from ord
where ordstts != '취소' and ordstts != '완료'
order by ordno desc
)
where rownum <= #{endRowNo}
)
where rnum >= #{startRowNo}
