mapper.select:
select * from ( select qnano, memno, qnatitle, qnacontent, qnadate, qnahitcnt, qnalock, lag(qnatitle,1,-1) over(order by qnano) as pretitle, lag(qnano,1,-1) over(order by qnano) as preno, lag(memno,1,-1) over(order by qnano) as prememno, lag(qnalock,1,-1) over(order by qnano) as prelock, lead(qnatitle,1,-1) over(order by qnano) as nexttitle,lead(qnano,1,-1) over(order by qnano) as nextno, lead(memno,1,-1) over(order by qnano) as nextmemno, lead(qnalock,1,-1) over(order by qnano) as nextlock from qna
where qnatitle like '%' || #{keyword} || '%' or qnacontent like '%' || #{keyword} || '%'
) where qnano = #{qnano}

mapper.update:
update qna set
	qnahitcnt = qnahitcnt + 1
	where qnano = #{qnano}

update qna set
	qnatitle=#{qnatitle},
	qnacontent=#{qnacontent}
	where qnano = #{qnano}

mapper.select:
select count(*) from ( select qnatitle, qnacontent from qna
where qnatitle like '%' || #{keyword} || '%' or qnacontent like '%' || #{keyword} || '%'
)


select rnum, qnano, qnatitle, qnacontent, memno, qnadate, qnahitcnt, qnalock
from (
    select rownum as rnum, qnano, qnatitle, qnacontent, memno, qnadate, qnahitcnt, qnalock
    from (
        select qnano, qnatitle, qnacontent, memno, qnadate, qnahitcnt, qnalock
        from qna

        	where qnatitle like '%' || #{keyword} || '%'
			or qnacontent like '%' || #{keyword} || '%'

        order by qnano desc
    )
    where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}
