mapper.select:
select * from( select rownum as rnum, qnano, memno, qnatitle, qnacontent, qnadate, qnahitcnt, qnalock, qnawriter, pretitle, preno, prememno, prelock, nexttitle, nextno, nextmemno, nextlock from ( select qnano, qna.memno, qnatitle, qnacontent, qnadate, qnahitcnt, qnalock, memid as qnawriter, lag(qnatitle,1,-1) over(order by qnano) as pretitle, lag(qnano,1,-1) over(order by qnano) as preno, lag(qna.memno,1,-1) over(order by qnano) as prememno, lag(qnalock,1,-1) over(order by qnano) as prelock, lead(qnatitle,1,-1) over(order by qnano) as nexttitle,lead(qnano,1,-1) over(order by qnano) as nextno, lead(qna.memno,1,-1) over(order by qnano) as nextmemno, lead(qnalock,1,-1) over(order by qnano) as nextlock from qna , member where qna.memno = member.memno
and (qnatitle like '%' || #{keyword} || '%' or qnacontent like '%' || #{keyword} || '%')
order by qnano desc ) ) where qnano = #{qnano}

mapper.update:
update qna set
	qnahitcnt = qnahitcnt + 1
	where qnano = #{qnano}

update qna set
	qnatitle=#{qnatitle},
	qnacontent=#{qnacontent}
	where qnano = #{qnano}

mapper.select:
select count(*) from ( select qnatitle, qnacontent from qna
where qnatitle like '%' || #{keyword} || '%' or qnacontent like '%' || #{keyword} || '%'
)


select *
from (
    select rownum as rnum, qnano, qnatitle, qnacontent, memno, qnadate, qnahitcnt, qnalock, memid as qnawriter
    from (
        select qnano, qnatitle, qnacontent, qna.memno as memno, qnadate, qnahitcnt, qnalock, memid
        from qna, member
        where qna.memno = member.memno

        	and (qnatitle like '%' || #{keyword} || '%'
			or qnacontent like '%' || #{keyword} || '%')

        order by qnano desc
    )
    where rownum <= #{pager.endRowNo}
)
where rnum >= #{pager.startRowNo}
